    <>
      <h1>Favorites</h1>
      <Row>
        <Col sm={12}>
          <ul style={{ listStyle: "none" }}>
            {cart.map((book, i) => (
              <li key={i} className="my-4">
                <Button
                  variant="danger"
                  onClick={() => {
                    dispatch({
                      type: "REMOVE_FROM_CART",
                      payload: i,
                    });
                  }}
                >
                  <FaTrash />
                </Button>
                <img
                  className="book-cover-small"
                  src={book.imageUrl}
                  alt="book selected"
                />
                {book.title}
              </li>
            ))}
          </ul>
        </Col>
      </Row>
    </>
  );
};

export default Favorites;
